mapper.select:
SELECT industryname,rounda,roundb,roundc,roundd,rounde,roundf,roundg from (SELECT industryname,SUM(eventnum) AS total, SUM(case when trim(round)='种子/天使/PreA轮' THEN eventnum ELSE 0 END) as rounda, SUM(CASE WHEN TRIM(round)='A/A+轮' THEN eventnum ELSE 0 END) as roundb, SUM(CASE WHEN TRIM(round)='PreB/B/B+轮' then eventnum ELSE 0 END) as roundc, SUM(CASE WHEN TRIM(round)='C轮' THEN eventnum ELSE 0 END) as roundd, SUM(CASE WHEN TRIM(round)='D轮' THEN eventnum ELSE 0 END) as rounde, SUM(CASE WHEN TRIM(round)='D轮以上' THEN eventnum ELSE 0 END) as roundf, SUM(CASE WHEN TRIM(round)='其他' THEN eventnum ELSE 0 END) as roundg FROM app_event_industry_month
year = #{year,jdbcType=INTEGER}
month = #{month,jdbcType=INTEGER}
GROUP BY industryid ) t ORDER BY t.total DESC
