mapper.select:
select bb.name as moneyRange, GROUP_CONCAT((case when cc.eventNum is null then 0 ELSE eventNum end) ORDER BY aa.id asc ) as eventNum, GROUP_CONCAT(aa.name ORDER by aa.id asc ) as roundName from (select id , name from app_invest_round where status=2 and id not in (50,51)) as aa join (select id,name from app_invest_interval) as bb left join ( select roundId,intervalId ,sum(eventNum) as eventNum from app_round_amount_interval where timeType = #{timeType}
and industryId=#{industryId}
group by intervalId,roundId )as cc on aa.id = cc.roundId and bb.id= cc.intervalId group by bb.id order by bb.id
